import { Fragment, useEffect, useState } from "react";
import Item from "../components/Item";



const App = () => {

  const [city, setCity] = useState("");

  const arak = [
    ["Halberd", "400gp"],
    ["Brass armor", "150gp"],
    ["Morning star", "90gp"],
    ["Battle hammer", "120gp"],
    ["Plate armor", "400gp"],
    ["Steel shield", "80gp"],
    ["Battle shield", "95gp"] 
  ];

  const osaris = [
    ["Double axe", "260gp"],
    ["Steel helmet", "293gp"]
  ];

  const garrogat = [
    ["Clerical mace", "170gp"],
    ["Crystal ball", "190gp"],
    ["Mind stone", "170gp"],
    ["Iron helmet", "150gp"],
    ["Viking shield", "85gp"]
  ];

  const abukir = [
    ["Bow", "130gp"],
    ["Crossbow", "160gp"]
  ];

  const lucindel = [
    ["Plate legs", "115gp"]
  ];

  return (
    <section 
      style={{
      display: "flex",
      width: "100vw",
      height: "100vh",
      alignItems: "center",
      flexDirection: "column",
      marginTop: "100px"
    }}>
      <div style={{ marginBottom: "100px", display: "flex", justifyContent: "space-evenly"}}>
        <button style={{ margin: "0 10px", width: "100px", height: "35px"}} onClick={() => { setCity("arak")}}>Arak</button>
        <button style={{ margin: "0 10px",width: "100px", height: "35px"}} onClick={() => { setCity("osaris")}}>Osaris</button>
        <button style={{ margin: "0 10px",width: "100px", height: "35px"}} onClick={() => { setCity("garrogat")}}>Garrogat</button>
        <button style={{ margin: "0 10px",width: "100px", height: "35px"}} onClick={() => { setCity("lucindel")}}>Lucindel</button>
        <button style={{ margin: "0 10px",width: "100px", height: "35px"}} onClick={() => { setCity("abukir")}}>Abukir</button>
      </div>

      <ul style={{ listStyle: "none"}}>
      {city == "arak" && arak.map((item, i) => {
        return (
          <Fragment key={i}>
            <div style={{ display: "flex" }} >
              <Item style={{ fontSize: "24px", width: "200px", margin: "5px 0"}} item={item[0]}></Item>
              <Item style={{ fontSize: "24px", margin: "5px 0" }}>{item[1]}</Item>
              
            </div>
            <div style={{ width: "100%", height: "1px", backgroundColor: "black"}}></div>
          </Fragment>
        )
      })}

      {city == "osaris" && osaris.map((item, i) => {
        return (
          <Fragment key={i}>
            <div style={{ display: "flex" }} >
              <li style={{ width: "200px", margin: "5px 0"}}>{item[0]}</li>
              <li style={{ margin: "5px 0" }}>{item[1]}</li>
              
            </div>
            <div style={{ width: "100%", height: "1px", backgroundColor: "black"}}></div>
          </Fragment>
        )
      })}

      {city == "garrogat" && garrogat.map((item, i) => {
        return (
          <Fragment key={i}>
            <div style={{ display: "flex" }} >
              <li style={{ width: "200px", margin: "5px 0"}}>{item[0]}</li>
              <li style={{ margin: "5px 0" }}>{item[1]}</li>
              
            </div>
            <div style={{ width: "100%", height: "1px", backgroundColor: "black"}}></div>
          </Fragment>
        )
      })}

      {city == "abukir" && abukir.map((item, i) => {
        return (
          <Fragment key={i}>
            <div style={{ display: "flex" }} >
              <li style={{ width: "200px", margin: "5px 0"}}>{item[0]}</li>
              <li style={{ margin: "5px 0" }}>{item[1]}</li>
              
            </div>
            <div style={{ width: "100%", height: "1px", backgroundColor: "black"}}></div>
          </Fragment>
        )
      })}

      {city == "lucindel" && lucindel.map((item, i) => {
        return (
          <Fragment key={i}>
            <div style={{ display: "flex" }} >
              <li style={{ width: "200px", margin: "5px 0"}}>{item[0]}</li>
              <li style={{ margin: "5px 0" }}>{item[1]}</li>
              
            </div>
            <div style={{ width: "100%", height: "1px", backgroundColor: "black"}}></div>
          </Fragment>
        )
      })}
      </ul>
    </section>
  )
}

export default App;
