import { Fragment, useEffect, useState } from "react";



const App = () => {

  const [city, setCity] = useState("");

  const arak = [
    ["Halberd", "400gp"],
    ["Brass armor", "150gp"],
    ["Morning star", "90gp"],
    ["Battle hammer", "120gp"],
    ["Plate armor", "400gp"],
    ["Steel shield", "80gp"],
    ["Battle shield", "95gp"] 
  ]

  return (
    <section 
      style={{
      display: "flex",
      width: "100vw",
      height: "100vh",
      justifyContent: "center",
      alignItems: "center",
      flexDirection: "column"
    }}>
      <div>
        <button>Arak</button>
        <button>Osaris</button>
        <button>Garrogat</button>
      </div>

      <ul style={{ listStyle: "none"}}>
      {city == "arak" && arak.map((item, i) => {
        return (
          <Fragment key={i}>
            <div style={{ display: "flex" }} >
              <li style={{ width: "200px", margin: "5px 0"}}>{item[0]}</li>
              <li style={{ margin: "5px 0" }}>{item[1]}</li>
              
            </div>
            <div style={{ width: "100%", height: "1px", backgroundColor: "black"}}></div>
          </Fragment>
        )
      })}
      </ul>
    </section>
  )
}

export default App;
