import { Global, css } from '@emotion/core'

const GlobalStyle = () => {
  return (
    <Global
      styles={css`
        html,
        body {
          margin: 0;
          padding: 0;
          box-sizing: border-box;
          overflow-x: hidden;
        }
        a {
          text-decoration: none;
          color: inherit;
        }

        p {
          font-family: 'Inter', sans-serif;
        }

      `}
    />
  )
}

export default GlobalStyle
